import React, { useEffect, useState } from 'react'
import { useSearchParams } from 'react-router-dom';
const AboutPage = () => {
    const [movie, setMovie] = useState({});
    const [searchparam]=useSearchParams()
    const id=JSON.parse(searchparam.get('object')).id ;
    const [loading,setLoading]=useState(true) ;
    useEffect(()=>{
          (async function getMovie(){
            const response = await fetch(`http://localhost:9000/api/movies/${id}`);
            const payload =await response.json();
            setMovie(payload);
            console.log(payload)
            setLoading(fals)
          }())
    },[])
    if(movie===undefined) return (<h1>wat</h1>)
  return (
    <div class="card" style={{width: "200px", margin:'10px'}} >
      <div class="card-body">
        <h5 class="card-title">{movie.title}</h5>
        <p class="card-text">{movie.overview}</p>
        <p class="card-text">{movie.tagline}</p>
        <p class="card-text">Runtime: {movie.runtime} minutes</p>
        <p class="card-text">Vote average: {movie.vote_average}</p>
        <p class="card-text">Vote Count: {movie.vote_count}</p>
        <p class="card-text">Status: {movie.status}</p>
        <p class="card-text">Release Date: {movie.release_date}</p>
      </div>
    </div>
  )
}

export default AboutPage
